import React, { useState } from 'react';
import './willread.css';
import 'material-icons';
import 'materialize-css'
import Card from '../../card/card';
import axios from 'axios';
import { encode, decode } from 'base-64';

export default function WillRead() {
    const [modal, showModal] = useState(false);
    const [bookCover, setCover] = useState('');
    const [bookTitle, setTtile] = useState('');
    const [bookDesc, setDesc] = useState('');

    async function handleForm(e) {
        e.preventDefault();
        if (e.target.bookCover != null) {
            setCover(encode(e.target.bookCover.files));
            setTtile(e.target.bookTitle.value);
            setDesc(e.target.bookDesc.value);
            await handleUpload();
        }
    }
    function handleUpload() {
       let formData = new formData()
        let bookData = {
            "bookCover": bookCover,
            "bookTitle": bookTitle,
            "bookDesc": bookDesc
        }
        const url = "http://localhost/backend/api.php"
        formData.append('dataJson', bookData)
        axios.post(url, formData)
        .then(res => console.log(res))
        .catch(err => console.log(err));
    }

    return (
        <div className='maindiv3'>
            <div className="formdiv3">
                <Card />
                <button className='btn3' onClick={() => (showModal(modal ? false : true))} ><span className="material-icons btn3">library_add</span></button>
                {
                    modal ?
                        <div className='modalBody'>
                            <div className="modal-content">
                                <form onSubmit={handleForm}>
                                    Book cover: <input placeholder="Book ISBN" type="file" id="bookCover" className="modalTextInput" />
                                    <input placeholder="Book Title" type="text" id="bookTitle" className="modalTextInput" />
                                    <input placeholder="Short description" id="bookDesc" type="text" className="modalTextInput" />
                                    <input className='btnSend' type="submit" value="add" />
                                    <button className='btnClose' onClick={() => (showModal(false))}>Cancel</button>
                                </form>
                            </div>
                        </div>
                        :
                        ''
                }
            </div>

        </div>
    )


}
