import React, { useState } from 'react';
import './willread.css';
import 'material-icons';
import 'materialize-css'
import Card from '../../card/card';





export default function WillRead() {
    const [modal, showModal] = useState(false);
    const [cover, setCover] = useState('');
    const [title, setTitle] = useState('');
    const [desc, setDesc] = useState('');

    function handleChange(e) {
        e.preventDefault();
        console.log(e.target.bookdesc.value)
    }
    return (
        <div className='maindiv3'>
            <div className="formdiv3">
                <Card />
                <button className='btn3' onClick={() => (showModal(modal ? false : true))} ><span className="material-icons btn3">library_add</span></button>
                {
                    modal ?
                        <div className='modalBody'>
                            <div className="modal-content">
                                <form onSubmit={handleChange}>
                                    <label>Book cover: </label>
                                    <input placeholder="Book Cover" id="bookcover" type="file" className="modalButtonInput" />
                                    <input placeholder="Book Title" id="booktitle" type="text" className="modalTextInput" />
                                    <input placeholder="Short description" id="bookdesc" type="text" className="modalTextInput" />
                                    <input type="submit" className='btnSend' value="Add" />
                                    <button className='btnClose' onClick={() => (showModal(false))}>Cancel</button>
                                </form>
                            </div>
                        </div>
                        :
                        ''
                }
            </div>

        </div>
    )


}
