    }

    return (
        <div className='maindiv3'>
            <div className="formdiv3">
                <Card />
                <button className='btn3' onClick={() => (showModal(modal ? false : true))} ><span className="material-icons btn3">library_add</span></button>
                {
                    modal ?
                        <div className='modalBody'>
                            <div className="modal-content">
                                <form onSubmit={handleBook}>
                                    Book cover: <input placeholder="Book ISBN" type="file" id="bookCover" className="modalTextInput" />
                                    <input placeholder="Book Title" type="text" id="bookTitle" className="modalTextInput" />
                                    <input placeholder="Short description" id="bookDesc" type="text" className="modalTextInput" />
                                    <input className='btnSend' type="submit" value="add" />
                                    <button className='btnClose' onClick={() => (showModal(false))}>Cancel</button>
                                </form>
                            </div>
                        </div>
                        :
                        ''
                }
            </div>

        </div>
    )


}
