import React, { useState } from 'react';
import './willread.css';
import 'material-icons';
import 'materialize-css'
import Card from '../../card/card';
var base64 = require('base-64');




export default function WillRead() {
    const [modal, showModal] = useState(false);


    return (
        <div className='maindiv3'>
            <div className="formdiv3">
                <Card/>
                <button className='btn3' onClick={() => (showModal(modal ? false : true))} ><span className="material-icons btn3">library_add</span></button>
                {
                    modal ?
                        <div className='modalBody'>
                            <div className="modal-content">
                                <label>Book cover: </label>
                                <input placeholder="Book Cover" type="file" className="modalButtonInput" />
                                <input placeholder="Book Title" type="text" className="modalTextInput" />
                                <input placeholder="Short description" type="text" className="modalTextInput" />
                                <button className='btnSend' onClick={() => (showModal(false))}>Add</button>
                                <button className='btnClose' onClick={() => (showModal(false))}>Cancel</button>
                            </div>
                        </div>
                        :
                        ''
                }
            </div>

        </div>
    )


}
